var amountScrolled=200;new WOW().init();$(window).scroll(function(){if($(window).scrollTop()>amountScrolled){$('a.sf-back-to-top').fadeIn('slow')}else{$('a.sf-back-to-top').fadeOut('slow')}});$('a.sf-back-to-top').click(function(){$('html, body').animate({scrollTop:0},700);return!1});$(window).bind("resize",function(){if($(this).width()<769){$('div').removeClass('bounceInLeft');$('div').removeClass('bounceInRight');$('div').removeClass('wow');$('div').removeClass('animated')}}).trigger('resize');$('a[href*="#"]').not('[href="#"]').not('[href="#0"]').click(function(event){if(location.pathname.replace(/^\//,'')==this.pathname.replace(/^\//,'')&&location.hostname==this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){event.preventDefault();$('html, body').animate({scrollTop:target.offset().top},1000)}}})